    private void getBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_getBActionPerformed
    double hoursWorkedj =  Double.parseDouble(hoursWorkedTf.getText());
    double rateOfPayj = Double.parseDouble(rateOfPayTf.getText());
    double ancerj = (hoursWorkedj * rateOfPayj);
    payTf.setText(ancerj +"");
    hoursWorkedTf.setText("");
    hoursWorkedTf.requestFocus();
    rateOfPayTf.setText("");
    if(ancerj >= 400)
    {
         JOptionPane.showMessageDialog(null ,"you have a good job");
    }
    else if(ancerj >= 200)
    {
         JOptionPane.showMessageDialog(null ,"you have a ok job");
    }
    else
    {
         JOptionPane.showMessageDialog(null ,"get a better job");
    }
    
    }//GEN-LAST:event_getBActionPerformed

