    private void goBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_goBActionPerformed
     double payj = Double.parseDouble(salaryTf.getText());
     double ancerj = payj;
     salaryTf.setText("");
     salaryTf.requestFocus();
     if (ancerj >= 50000)
     {
         ancerTa.setText("you are entitled" +"\n"+" to a 5% pay rise");
     }
     else if (ancerj >= 40000)
     {
         ancerTa.setText("you are entitled" +"\n"+" to a 10% pay rise");
     }
     else if (ancerj >= 20000)
     {
         ancerTa.setText("you are entitled" +"\n"+"to a 15% pay rise");
     }
     else 
     {
         ancerTa.setText("you are entitled"+"\n" +"to a 20% pay rise");
     }
    }//GEN-LAST:event_goBActionPerformed

